mapper.select:
select


from bus_data a where 1=1  

    and data_id = #{data_id,jdbcType=VARCHAR}


   and batch_id = #{batch_id,jdbcType=VARCHAR}


   and user_code like #{user_code,jdbcType=VARCHAR}


	and a.col5 >= str_to_date(#{col5_start,jdbcType=VARCHAR},'%Y-%m-%d')


	and a.col5 <= str_to_date(#{col5_end,jdbcType=VARCHAR},'%Y-%m-%d')


	and a.col6 >= str_to_date(#{col6_start,jdbcType=VARCHAR},'%Y-%m-%d')


	and a.col6 <= str_to_date(#{col6_end,jdbcType=VARCHAR},'%Y-%m-%d')


   and col1 like #{col1,jdbcType=VARCHAR}

  and bus_id = #{bus_id,jdbcType=VARCHAR}    
 order by batch_id,create_date

mapper.delete:
delete from bus_data
where  1=1 

    and data_id = #{data_id,jdbcType=VARCHAR}


   and batch_id = #{batch_id,jdbcType=VARCHAR}


   and user_code = #{user_code,jdbcType=VARCHAR}


   and bus_id = #{bus_id,jdbcType=VARCHAR}

mapper.select:
	select item_id, template_id, item_name, item_type, data_col, rule_id, order_id,is_display,col_index,is_export,display_width
	   from bus_template_item
where 1=1 

    and item_id = #{item_id,jdbcType=INTEGER}


    and template_id = #{template_id,jdbcType=VARCHAR}


    and item_name = #{item_name,jdbcType=VARCHAR}


    and item_type = #{item_type,jdbcType=VARCHAR}


    and data_col = #{data_col,jdbcType=VARCHAR}


    and rule_id = #{rule_id,jdbcType=INTEGER}


    and order_id = #{order_id,jdbcType=INTEGER

  order by order_id

select
	rule_id, rule_name, rule_type, rule_content, create_date
from bus_rule
where 1=1 

    and rule_id = #{rule_id,jdbcType=INTEGER} 


    and rule_name = #{rule_name,jdbcType=VARCHAR} 


    and rule_type = #{rule_type,jdbcType=VARCHAR}


    and rule_content = #{rule_content,jdbcType=VARCHAR}


    and create_date = #{create_date,jdbcType=TIMESTAMP}

select
	bus_id, bus_name, imp_tmp, exp_tmp, create_date
  from bus_info where 1=1 

    and bus_id = #{bus_id,jdbcType=INTEGER}


    and bus_name = #{bus_name,jdbcType=VARCHAR}


    and imp_tmp = #{imp_tmp,jdbcType=VARCHAR}


    and exp_tmp = #{exp_tmp,jdbcType=VARCHAR}


    and create_date = #{create_date,jdbcType=TIMESTAMP}

select 
	template_id, template_name, template_type, data_type, sheet_number, start_row, create_date
from bus_template where 1=1

    and template_id = #{template_id,jdbcType=VARCHAR}


  and template_name = #{template_name,jdbcType=VARCHAR}


  and template_type = #{template_type,jdbcType=VARCHAR}


  and data_type = #{data_type,jdbcType=VARCHAR}


  and sheet_number = #{sheet_number,jdbcType=INTEGER}


  and start_row = #{start_row,jdbcType=INTEGER}


  and create_date = #{create_date,jdbcType=TIMESTAMP}
